import React from 'react';
import ReactDOM from 'react-dom';

ReactDOM.render(
  <App1 />
  ,
  document.getElementById('root')
);

function App1() {
  return ( // returnの中は1要素だけ。複数入れたい場合は外側をdivなどで囲む
    <div className="App1"> {/* classではなくclassNameを用いる */}
      <li>hoge</li>
      <li>
        fuga<br /> {/* JSXでは空要素の閉じスラッシュが必須 */}
        fuga2
      </li>
      <li>piyo</li>
    </div>
  );
}

// フラグメントを利用すれば余計なノードを出力せず複数要素をまとめられる
function App2() {
  return (
    <>
      <li>hoge</li>
      <li>fuga</li>
    </>
  );
}

// styleの指定方法。外側の括弧は「JSを書く」合図。内側はオブジェクト。このようにJSのオブジェクトとしてstyleを指定する。
// このfontSizeのようにピュアな数値は自動的にpxがつく。別の単位（%など）の場合は文字列で与える。
function App3() {
  return (
    <h1 style={{ fontSize: 16, width: "80%", color: "red" }}>hoge</h1>
  );
}
