// 入力フォーム & ボタンクリックで入力内容を反映
const App17 = () => {
  // 入力中テキスト
  const [inputText, setInputText] = useState("");
  // 表示テキスト
  const [text, setText] = useState("React");

  // 入力イベント
  const handleChange = (e) => setInputText(e.target.value);
